<?php//Tabber   /** * Tabber Class */class loo_tab extends WP_Widget {    /** 构造函数 */    function loo_tab() {        parent::WP_Widget(false, $name = 'Loome-文章Tab');     }    /** @see WP_Widget::widget */    function widget($args, $instance) {        global $wpdb;        extract( $args );        ?>		  <div class="widget box row">		  	<div id="tab-title">				<div class="tab">					<ul id="tabnav">						<li>最近更新</li>						<li class="selected">最热种子</li><!--						<li>专题</li>-->					</ul>				</div>				<div class="clear"></div>			</div>			<div id="tab-content">				<ul class="hide">                    <?php                    $sql = " select p.* from " . $wpdb->prefix . "auto_movie m inner join " . $wpdb->prefix . "posts p on m.postid=p.ID where p.post_type='post' and post_status='publish' order by m.a_upTime desc limit 10; ";                    $results = $wpdb->get_results($sql);                    // var_dump($results);                    if ($results) {                        foreach ($results as $key => $value) {                            ?>                            <li><a href="<?php the_permalink($value->ID); ?>" title="<?php echo $value->post_title; ?>"><?php echo $value->post_title; ?></a></li>                            <?php                        }                    }                    ?>                </ul>                <ul>                    <?php                    $sql = " select p.* from " . $wpdb->prefix . "auto_movie m inner join " . $wpdb->prefix . "posts p on m.postid=p.ID where p.post_type='post' and post_status='publish' order by m.maxSeeders desc limit 10; ";                    $results = $wpdb->get_results($sql);                    // var_dump($results);                    if ($results) {                        foreach ($results as $key => $value) {                            ?>                            <li><a href="<?php the_permalink($value->ID); ?>" title="<?php echo $value->post_title; ?>"><?php echo $value->post_title; ?></a></li>                            <?php                        }                    }                    ?>                </ul>                <ul class="hide">                    <?php                    if(1==2){                        $sql = " select p.* from " . $wpdb->prefix . "auto_movie m inner join " . $wpdb->prefix . "posts p on m.postid=p.ID where p.post_type='post' and post_status='publish' and (thematic='1' or thematic is null or thematic='0') order by thematic desc,m.id desc limit 10; ";                        $results = $wpdb->get_results($sql);                        // var_dump($results);                        if ($results) {                            foreach ($results as $key => $value) {                                ?>                                <li><a href="<?php the_permalink($value->ID); ?>" title="<?php echo $value->post_title; ?>"><?php echo $value->post_title; ?></a></li>                                <?php                            }                        }                    }                    ?>                </ul>             </div>          </div><?php    }    /** @see WP_Widget::update */    function update($new_instance, $old_instance) {               return $new_instance;    }    /** @see WP_Widget::form */    function form($instance) {                $title = esc_attr($instance['title']);    }    }add_action('widgets_init', create_function('', 'return register_widget("loo_tab");'));?>